import React from "react";
import classes from "./Sidebar.module.css";
import MenuItem from "./MenuItem/MenuItem";

const sidebar = (props) => {
  let data = [
    { link: "HOME", subLink: ["Home Archive"] },
    {
      link: "PORTFOLIO",
      subLink: ["PitchBook", "Case Studies", "Illustrations"],
    },
    {
      link: "SHOP",
      subLink: ["shirt/apparel", "posters", "stickers", "downloads"],
    },
    {
      link: "Links",
      subLink: [
        "Instagram",
        "LinkedIn",
        "Myspace",
        "totally legit reviews",
        "join the email list?",
      ],
    },
  ];

  let AllItems = data.map((dataItem, index) => {
    return (
      <div className={classes.NavList}>
        <ul>
          <li key={index}>
            <div className={classes.Header}>
              <div className={classes.MainGroup}>
                <a href="/">{dataItem.link}</a>
              </div>
            </div>

            {dataItem.subLink.map((arrayItem, index) => {
              return (
                <div className={classes.Sublinks}>
                  <ul className={classes.Sublinks}>
                    <li>{arrayItem}</li>
                  </ul>
                </div>
              );
            })}
          </li>
        </ul>
      </div>
    );
  });
  //THIS IS WHERE IM RETURING THE WHOLE SIDE BAR
  return (
    <React.Fragment>
      <div className={classes.Sidebar}>{AllItems}</div>
    </React.Fragment>
  );
};
export default sidebar;
