import React from "react";
import classes from "./FeedBar.module.css";
const feedBar = (props) => {
  let blockColor;
  //basic css application to different art types
  if (props.section == "portfolio") {
    blockColor = "lightgreen";
  } else if (props.section == "shop") {
    blockColor = "lightpink";
  } else if (props.section == "social") {
    blockColor = "lightblue";
  }

  if (!props.viewingThread) {
    return <div></div>;
  } else {
    return (
      <div style={{ background: blockColor }} className={classes.MainBar}>
        {props.children}
      </div>
    );
  }
};

export default feedBar;
